CRUDEntity.SelectSQL:
SELECT `Id`,
`Code`,
`Reason`,
`ChangeDescription`,
`CreatorId`,
`CreateTime`,
`ChangePlan`,
`RollbackPlan`,
`BeginTime`,
`EndTime`,
`ResponsibilityUserId`,
`RentId`,
`RiskLevel`,
`RiskAssessor`,
`Title`,
`CheckList` 
 FROM wf_form_changemanagement WHERE 1=1 
{? AND `Id` like '%$Id$%' } 
{? AND `Code` like '%$Code$%' } 
{? AND `Reason` like '%$Reason$%' } 
{? AND `ChangeDescription` like '%$ChangeDescription$%' } 
{? AND `CreatorId` like '%$CreatorId$%' } 
{? AND DATE_FORMAT(`CreateTime`,'%Y-%m-%d') = #CreateTime# } 
{? AND `ChangePlan` like '%$ChangePlan$%' } 
{? AND `RollbackPlan` like '%$RollbackPlan$%' } 
{? AND DATE_FORMAT(`BeginTime`,'%Y-%m-%d') = #BeginTime# } 
{? AND DATE_FORMAT(`EndTime`,'%Y-%m-%d') = #EndTime# } 
{? AND `ResponsibilityUserId` like '%$ResponsibilityUserId$%' } 
{? AND `RentId`=#RentId# } 
{? AND `RiskLevel` like '%$RiskLevel$%' } 
{? AND `RiskAssessor` like '%$RiskAssessor$%' } 
{? AND `Title` like '%$Title$%' } 
{? AND `CheckList` like '%$CheckList$%' }

