CRUDEntity.UpdateSQL:
UPDATE ep_express SET `Id`=#Id#
{?? ,`CreatorId`=#CreatorId# }
{?? ,`CreatorDeptId`=#CreatorDeptId# }
{?? ,`CreateTime`=#CreateTime# }
{?? ,`Price`=#Price# }
{?? ,`Number`=#Number# }
{?? ,`ToCompany`=#ToCompany# }
{?? ,`ToUser`=#ToUser# }
{?? ,`ToAddress`=#ToAddress# }
{?? ,`Description`=#Description# }
{?? ,`ExpressType`=#ExpressType# }
{?? ,`File`=CAST(#File# AS SIGNED) }
 WHERE `Id`=#Id#

CRUDEntity.SelectSQL:
SELECT `Id`,
`CreatorId`,
`CreatorDeptId`,
`CreateTime`,
`Price`,
`Number`,
`ToCompany`,
`ToUser`,
`ToAddress`,
`Description`,
`ExpressType`,
`File` 
 FROM ep_express WHERE 1=1 
{? AND `Id` like '%$Id$%' } 
{? AND `CreatorId` like '%$CreatorId$%' } 
{? AND `CreatorDeptId` like '%$CreatorDeptId$%' } 
{? AND DATE_FORMAT(`CreateTime`,'%Y-%m-%d') = #CreateTime# } 
{? AND `Price`=#Price# } 
{? AND `Number` like '%$Number$%' } 
{? AND `ToCompany` like '%$ToCompany$%' } 
{? AND `ToUser` like '%$ToUser$%' } 
{? AND `ToAddress` like '%$ToAddress$%' } 
{? AND `Description` like '%$Description$%' } 
{? AND `ExpressType` like '%$ExpressType$%' } 
{? AND `File`=#File# }
@security{EXPRESS_LIST}

